0..5 |Where-Object { $_ % 1 -eq 0}| %{$length = $_; (gci -include *.cs,*.html, *.css, *.ts -recurse | ?{ $_.fullname -notmatch "\\(node_modules|assets|obj|folder\\subfolder|folder\\wildcard.*wildcard)\\?" } | select-string . | where {$thisLength = $_.Line.Trim().Length; $lengthBig = $length + 5; $thisLength -gt $length -and $thisLength -lt $lengthBig }).Count |%{"${length}:${lengthBig};${_}"}}